#
# Function "GetCurrEq"
#

CREATE FUNCTION `GetCurrEq`(aValue numeric(12,2), aValCurrId int(11), aEqCurrId int(11), aDate Date) RETURNS decimal(12,2)
BEGIN
  IF aValCurrId = aEqCurrId THEN  	
    RETURN aValue;
  ELSE 
    SELECT bid, count(*) as rowcount FROM exchange WHERE first_curr_id=aEqCurrId AND second_curr_id=aValCurrId AND act_date=(
      SELECT max(act_date) FROM exchange WHERE first_curr_id=aEqCurrId AND second_curr_id=aValCurrId AND act_date<=aDate
    ) INTO @rate, @rowcount;
    IF @rowcount>0 THEN 
      RETURN @rate*aValue; 
    ELSE
      SELECT ask FROM exchange WHERE first_curr_id=aValCurrId AND second_curr_id=aEqCurrId AND act_date=(
        SELECT max(act_date) FROM exchange WHERE first_curr_id=aValCurrId AND second_curr_id=aEqCurrId AND act_date<=aDate
      ) INTO @rate; 
      RETURN aValue/@rate;   
    END IF;
  END IF;
END;

