BEGIN
    select sum(sum_value) from (
        select GetCurrEq(t.value, fa.curr_id, aCurrID, t.value_date) as sum_value  
        from transactions t
        join fin_accounts fa on fa.id=t.deb_acc_id
        where t.article_id=aArticleID
        and t.value_date>=aBeginDate and t.value_date<=aEndDate
    ) z into @sum_value;
    RETURN @sum_value;
END;

#
# Function "GetTextByLid"
#

CREATE FUNCTION `GetTextByLid`(`aLid` int(11), aLangCode varchar(4)) RETURNS varchar(255) CHARSET utf8
BEGIN
  SELECT value, count(*) as rowcount FROM translations WHERE lid=aLid AND lang_code=aLangCode INTO @txt, @rowcount;
  IF @rowcount = 1 THEN
     RETURN @txt;
  END IF; 
   
  IF @rowcount=0 AND aLangCode<>"en" THEN  
     SELECT value, count(*) as rowcount FROM translations WHERE lid=aLid AND lang_code="en" INTO @txt, @rowcount;
     IF @rowcount = 1 THEN
        RETURN @txt;
     END IF; 
  END IF;
  
  IF @rowcount=0 THEN  
     SELECT value, count(*) as rowcount FROM translations WHERE lid=aLid LIMIT 1 INTO @txt, @rowcount;
     IF @rowcount = 1 THEN
        RETURN @txt;
     END IF; 
  END IF;
  
  IF @rowcount=0 THEN RETURN "not defined"; 
  END IF;
END;

